clear;close all;inputImage = imread('Fig0431(d)(blown_ic_crop).tif');resizedImage = imresize(inputImage,256/1026);input = im2single(resizedImage);[M,N] = size(input);paddingImage = zeros(M*2,N*2,'single'); for x=1:M    for y=1:N        paddingImage(x,y) = input(x,y);    endendcenterImage = zeros(M*2,N*2,'single'); for x=1:M*2    for y=1:N*2        centerImage(x,y) = paddingImage(x,y)*((-1)^(x+y));    endendF = myFFT2(centerImage);D0=10;H = myGLPF(D0,M*2,N*2);G = H.*F;temp_gp=real(myIFFT2(G));gp = temp_gp;for x=1:M*2    for y=1:N*2        gp(x,y) = temp_gp(x,y)*((-1)^(x+y));    endendoutputImage = zeros(M,N,'single');for x=1:M    for y=1:N        outputImage(x,y) = gp(x,y);    endendsubplot(2,4,1);imshow(input)subplot(2,4,2);imshow(paddingImage)subplot(2,4,3);imshow(centerImage)subplot(2,4,4);imshow(log(1+abs(F)),[])subplot(2,4,5);imshow(log(1+abs(H)),[])subplot(2,4,6);imshow(log(1+abs(G)),[])subplot(2,4,7);imshow(gp)subplot(2,4,8);imshow(outputImage)