clear;close all;inputImage = imread('Fig0441(a)(characters_test_pattern).tif');[M, N] = size(inputImage);D0 = 7;D2 = 10;D3 = 20;D4 = 35;D5 = 100;ff=zeros(M,N);for x=1:M    for y=1:N        ff(x,y) = inputImage(x,y)*((-1)^(x+y));    endendh1 = myGLPF(D0,M,N);h2 = myGLPF(D2,M,N);h3 = myGLPF(D3,M,N);h4 = myGLPF(D4,M,N);h5 = myGLPF(D5,M,N);f=fft2(ff);g1 = h1.*f;g2 = h2.*f;g3 = h3.*f;g4 = h4.*f;g5 = h5.*f;outputImage1 = abs(ifft2(g1));outputImage2 = abs(ifft2(g2));outputImage3 = abs(ifft2(g3));outputImage4 = abs(ifft2(g4));outputImage5 = abs(ifft2(g5));subplot(2,3,1), imshow(inputImage);title('1')subplot(2,3,2), imshow(outputImage1,[]);title('2')subplot(2,3,3), imshow(outputImage2,[]);title('3')subplot(2,3,4), imshow(outputImage3,[]);title('4')subplot(2,3,5), imshow(outputImage4,[]);title('5')subplot(2,3,6), imshow(outputImage5,[]);title('6')