clear;close all;inputImage = imread('Fig0441(a)(characters_test_pattern).tif');[M, N] = size(inputImage);D0 = 60;D2 = 160;ff=fft2(inputImage);f=fft2(inputImage);m=M/2;n=N/2;for x=1:m    for y=1:n        f(x,y) = ff(x+m,y+n);    endendfor x=m+1:M    for y=n+1:N        f(x,y) = ff(x-m,y-n);    endendfor x=m+1:M    for y=1:n        f(x,y) = ff(x-m,y+n);    endendfor x=1:m    for y=n+1:N        f(x,y) = ff(x+m,y-n);    endendh1 = myGHPF(D0,M,N);h2 = myGHPF(D2,M,N);g1 = h1.*f;g2 = h2.*f;outputImage1 = abs(ifft2(g1));outputImage2 = abs(ifft2(g2));subplot(1,3,1), imshow(inputImage);title('1')subplot(1,3,2), imshow(uint8(outputImage1));title('2')subplot(1,3,3), imshow(uint8(outputImage2));title('3')